SELECT *
FROM(
SELECT rownum r_num,a.* FROM (
SELECT * FROM evuser_t
WHERE EVU_STATUS = 0
ORDER BY EVU_IDX DESC
) a
) WHERE r_num BETWEEN
#{begin} AND #{end}

SELECT *
FROM(
SELECT rownum r_num,a.* FROM (
SELECT * FROM evuser_t
WHERE EVU_STATUS = 1
ORDER BY EVU_IDX DESC
) a
) WHERE r_num BETWEEN
#{begin} AND #{end}

SELECT
* FROM(
SELECT rownum r_num,a.* FROM(
SELECT * FROM evuser_t
WHERE
EVU_STATUS = 0 AND EVU_ID LIKE '%'||#{searchValue}||'%' 

ORDER BY evu_idx DESC
) a
) WHERE r_num BETWEEN #{begin} AND #{end}

SELECT
* FROM(
SELECT rownum r_num,a.* FROM(
SELECT * FROM evuser_t
WHERE
EVU_STATUS = 1 AND EVU_ID LIKE '%'||#{searchValue}||'%' 

ORDER BY evu_idx DESC
) a
) WHERE r_num BETWEEN #{begin} AND #{end}
