mapper.select:
SELECT *
FROM(
SELECT rownum r_num,a.* FROM(
SELECT * FROM evbbs_t
WHERE
evbbs_status = 0 AND evcategory_idx = #{evcategory_idx}
ORDER BY
evbbs_idx DESC
) a
)
WHERE r_num BETWEEN #{begin} AND #{end}

SELECT *
FROM(
SELECT rownum r_num,a.* FROM (
SELECT * FROM
evbbs_t
WHERE
evbbs_status = 0 AND evcategory_idx = #{evcategory_idx}
ORDER BY evbbs_idx DESC
) a
) WHERE r_num BETWEEN
#{begin} AND #{end}

SELECT COUNT (*) FROM evbbs_t
WHERE evcategory_idx = #{idx}
AND evbbs_status = 0

SELECT COUNT (*) FROM evbbs_t
WHERE evcategory_idx = #{idx}
AND evbbs_status = 1

mapper.update:
UPDATE evbbs_t set
evbbs_status = 1
WHERE evcategory_idx = #{idx }

UPDATE evbbs_t
SET evbbs_title = #{evbbs_title},
evbbs_content = #{evbbs_content},
evbbs_ip = #{evbbs_ip}

	,evbbs_file_name = #{evbbs_file_name}

WHERE evbbs_idx = #{evbbs_idx}

UPDATE evbbs_t set
evbbs_status = 1
WHERE evbbs_idx = #{evbbs_idx}

UPDATE evbbs_t set
evbbs_status = 0
WHERE evcategory_idx = #{idx }
